import { BsBookmarkStarFill, BsBookmarkStar } from "react-icons/bs";
import { useSelector } from 'react-redux'
import './GameList.css'
import { useDispatch } from 'react-redux'
import { deleteGame, addFavorite } from '../../redux/games/actionCreators'
const GameList = () =>{
    const games = useSelector((state) => state.games)
    const dispatch = useDispatch()
    const handleDeleteGame = (id) =>{
        dispatch(deleteGame(id))

    }
    return (
        <div className="app-block game-list">
        <h2>Game List </h2>
            {games.length === 0 ? (
                <p>no games played</p>
            ) : (
                <ul> 

                {games.map((game, i) => (
                    <li key={game.id}> 
                        <div className='game-info'> {++i}. {game.title}  <strong>{game.price}</strong></div>
                         {
                            game.isFavorite ?(
                                <BsBookmarkStarFill className="star-icon"/>
                            ) : (
                                <BsBookmarkStar className="star-icon"/>
                            )
                         }
                        <div className='game-actions'> <button  onClick={() => handleDeleteGame(game.id)}> Delete </button>  </div>
                    </li>
                ))}   

                </ul>
            )}


        </div>
    )
}
export default GameList
