import { createSlice, nanoid } from '@reduxjs/toolkit';


const initialState = [];

const gamesSlice = createSlice({
  name: 'games',
  initialState,
  reducers: {

      addGame: (state, action) => {
        state.push(action.payload);
      },
      deleteGame:(state, action) =>{
      const index = state.findIndex((book) => book.id === action.payload)
      }
    }})
